function toogleDisco(){const toggles=document.querySelectorAll(".main_nav_toggle");const toggleBtn=document.querySelector('.click_profile');toggleBtn.addEventListener('mouseenter',function(){toggleElements()});toggleBtn.addEventListener('mouseleave',function(){offElements()});window.addEventListener('resize',function(){if(window.innerWidth>1024){offElements()}});function toggleElements(){[].forEach.call(toggles,function(toggle){toggle.classList.toggle('on')})}function offElements(){[].forEach.call(toggles,function(toggle){toggle.classList.remove('on')})}}toogleDisco();