const sliderWrapper=document.getElementsByClassName('slider-wrapper');const sliderContainer=document.getElementsByClassName('slider-container');const slides=document.getElementsByClassName('slide');const slideCount=slides.length;const navPrev=document.getElementById('prev');const navNext=document.getElementById('next');let currentIndex=0;let topHeight=0;function calculateTallSlide(){for(let i=0;i<slideCount;i++){if(slides[i].offsetHeight>topHeight){topHeight=slides[i].offsetHeight}sliderWrapper[0].style.height=topHeight+'px';sliderContainer[0].style.height=topHeight+'px'}}calculateTallSlide();function rowSlide(){for(let i=0;i<slideCount;i++){slides[i].style.left=i*125+'%'}}rowSlide();function goToSlide(idx){sliderContainer[0].style.left=-125*idx+'%';sliderContainer[0].classList.add('animated');currentIndex=idx;updateNav()}goToSlide(0);function updateNav(){if(currentIndex==0){navPrev.classList.add('disabled')}else{navPrev.classList.remove('disabled')};if(currentIndex==slideCount-1){navNext.classList.add('disabled')}else{navNext.classList.remove('disabled')}}navPrev.addEventListener('click',function(event){event.preventDefault();goToSlide(currentIndex-1);console.log(currentIndex)});navNext.addEventListener('click',function(event){event.preventDefault();goToSlide(currentIndex+1);console.log(currentIndex)});